function onLoad() {
  // Using Try/Catch to trap runtime errors.  
  // The helloWorld() function does not exist.

	try{
		helloWorld();
	}

	catch(err){ // If you want to catch custom errors, use the JavaScript throw "text"; or throw number; method instead of catch(err). 
		jslog('A JavaScript runtime error occurred +err.message by Wei: ' + err.message);
		jslog('A JavaScript runtime error occurred + err.name by Wei: ' + err.name);
	} //The JavaScript err object has two useful properties:    message    name

	throw "throw by wei: A JavaScript runtime error occurred - helloWorld() function does not exist.";
}

